mapper.insert:
INSERT INTO customer (
	custid
	, password
	, name
	, email
	, division
	, idno
	, address
)
VALUES (
	#{custid}
	, #{password}
	, #{name}
	, #{email}
	, #{division}
	, #{idno}
	, #{address}
)

mapper.update:
UPDATE customer
SET password = #{password}
, name = #{name}
, email = #{email}
, division = #{division}
, idno = #{idno}
, address = #{address}
WHERE custid = #{custid}
